import React, { Component } from 'react'
import Quiz from './components/Quiz'
import axios from 'axios'

class App extends Component {
  state = {
    question: '',
    choice1: '',
    choice2: '',
    choice3: '',
    answer: '',
    quiz: [],
  }

  handleChange = (event) => {
      this.setState({
        [event.target.id]: event.target.value
      })
    }

    handleSubmit = (event) => {
      event.preventDefault()
      axios.post('/quiz', this.state).then((response) => {
        this.getQuestion()
      })
    }

    updateQuestion = (event) => {
      event.preventDefault()
      event.target.reset()
      const id = event.target.id
      axios.put('/quiz/' + id, this.state).then((response) => {
        this.getQuestion()
      })
    }

    deleteQuestion = (event) => {
      axios.delete('/quiz/' + event.target.value).then((response) => {
        this.getQuestion()
      })
    }

    getQuestion = () => {
      axios
      .get('/quiz')
      .then(
        (response) => this.setState({
          quiz: response.data,
          question: '',
          choice1: '',
          choice2: '',
          choice3: '',
          answer: ''
        }),
        (err) => console.error(err)
      )
      .catch((error) => console.error(error))
    }

    showAnswer=(event)=>{
      event.preventDefault()
      let answer = this.state.showAnswer
      axios.get('/quiz/' + event.target.id).then((response)=>{

          if(answer) {
            this.setState({
              showAnswer:false,
              quiz: response.data
            })
          } else {
            this.setState({
              showAnswer:true,
              quiz: response.data
            })
          }
      })
    }

    componentDidMount = () => {
      this.getQuestion()
    }

render = () => {
return (
  <div className='container'>
  <h1>Hello!</h1>
  <button value={quiz._id} onClick={this.showAnswer}>
    Show Answer</button>
  <h4>{ this.state.showAnswer ? 'Answer: ' + this.state.answer : null }</h4>
  <Quiz />
  </div>
    )
  }
}


export default App;
